import "./calculator.css";
import { useState } from "react";

export default function Calculator() {
    const [sum, setSum] = useState("");
    var [cal, setcal] = useState("");

    // const operation = (x, y) => {
    //         x * y
    //         x - y
    // }

    const clickHandler = (e) => {
        // input +=  e.target.innerText        // there is no declaration for the var "input" to accumulate

        const num = parseInt(e.target.innerText);
        if (!isNaN(num)) {
            setSum(sum + e.target.innerText);
        } else {
            const op = e.target.value;
            setcal((cal = e.target.innerText));
        }
        switch (e.target.value) {
            case "0":
                return setSum("0");
                break;
            case "%":
                return setSum("0");
                break;
            case "=÷":
                return setSum("0");
                break;
            case "×":
                return setSum("0");
                break;
            case "-":
                return setSum("0");
                break;
            case "%":
                return setSum("0");
                break;
            case "=":
                return setSum("0");
                break;
        }
    };

    return (
        <>
            <div className="calCon">
                <div className="sumCon">
                    <div className="cal">{cal}</div>
                    <div className="sum">{sum}</div>
                </div>
                <ul
                    className="keyCon"
                    onClick={(e) => {
                        clickHandler(e);
                    }}
                >
                    <li className="key" value="0">
                        AC
                    </li>
                    <li className="key">&plusmn;</li>
                    <li className="key" value="%">
                        %
                    </li>
                    <li className="key-right" value="=÷">
                        &divide;
                    </li>
                    <li className="key">7</li>
                    <li className="key">8</li>
                    <li className="key">9</li>
                    <li className="key-right" value="×">
                        &times;
                    </li>
                    <li className="key">4</li>
                    <li className="key">5</li>
                    <li className="key">6</li>
                    <li className="key-right" value="-">
                        -
                    </li>
                    <li className="key">1</li>
                    <li className="key">2</li>
                    <li className="key">3</li>
                    <li className="key-right" value="%">
                        +
                    </li>
                    <li className="zero key">0</li>
                    <li className="key">.</li>
                    <li className="key-right" value="=">
                        =
                    </li>
                </ul>
            </div>
        </>
    );
}
